import React, { Component } from 'react';
import './app-container.css';
import { Navbar, Container, Row, Col, Nav } from 'react-bootstrap';

import PostsContainer from './../PostContainer/post-container.js';

class AppContainer extends Component {
    constructor(props, context) {
        super(props, context);

        this.onTabbarSelect = this.onTabbarSelect.bind(this);

        this.state = {
            tabbarKey: 1,
        }
    }

    onTabbarSelect(k) {
        return this.setState({tabbarKey: k});
    }

    render() {
        return (
            <div>
                <Navbar bg="light" fixed="top">
                    <Navbar.Brand align="center">
                        <span className="navbar-brand-name">
                            Slacts
                        </span>
                        <span className="navbar-brand-sub">
                            - selfies of acts worth sharing
                        </span>
                    </Navbar.Brand>
                </Navbar>
                <Container>
                    <Row>
                        <Col xs={0} md={3}>
                        </Col>
                        <Col xs={12} md={6}>
                            <PostsContainer />
                        </Col>
                        <Col xs={0} md={3}>
                        </Col>
                    </Row>
                </Container>


                <Navbar bg="dark" fixed="bottom">
                    <Container fluid>
                        <Row noGutters>
                            <Col xs={6} md={6}>
                                Hello
                            </Col>
                            <Col xs={6} md={6}>
                                World
                            </Col>

                        </Row>
                    </Container>
                </Navbar>
            </div>
        );
    }
}

export default AppContainer;
